import React, { useState, useEffect } from "react";
import socketIOClient from 'socket.io-client'


const io = socketIOClient("wss://apizawaj.onebaz.com",{ path:'/socket/v1/socketio', query: { token: "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ0eXBlT2ZVc2VyIjoibWVtYmVyIiwiaWQiOiI2MzE4Yjg3NjY2ZDY5NTJlYjI4MGQ4MTQiLCJrZXkiOiJzQnk2Qm8zR0RtR0Z2YmswMUVDZHlUbVBaUm55UlA0diIsImlhdCI6MTY2NTUxNjY4NSwiZXhwIjoxNjgxMDY4Njg1fQ.tqvvYBEQ0vRiOjCfQ97q5c6NGBFHEk9xB2SHQ_gIips"  } });


const userName = 'User '+parseInt(Math.random()*10)
function App() {
  const [message, setMessage] = useState('')
  const [chat, setChat] = useState([])

  useEffect(() => {
    io.on('member/is-moderation-open', payload => {
      setChat([...chat, payload])
    })
  })

  const sendMessage = (e) => {
    e.preventDefault();
    console.log(message)
    io.emit('message',{userName,message})
    setMessage('')
  };
  return (
    <div className="App">
      <h1>Welcome to chat app</h1>
      <form onSubmit={sendMessage}>
        <input type="text" name="message"
        placeholder='Type message'
        value={message}
        onChange={(e)=>{setMessage(e.target.value)}}
        required
        ></input>
        <button type='submit'>Send</button>
      </form>
      {chat.map((payload, index)=>{
        return(
          <h3 key={index}>{payload.userName}: <span>{payload.message}</span></h3>
        )
      })}
    </div>
  );
}

export default App;
